stop();
var number:Number=1;
var numbertotal:Number=0;
var numberselected:Number=0;
bar.bar.scaleX=0;
var changesnotsaved:Boolean=false;
var txtloaded:Boolean=false;
var triedload:Boolean=false;
var myTextLoader:URLLoader = new URLLoader();
var imgerror:noneimg = new noneimg();
myTextLoader.dataFormat=URLLoaderDataFormat.VARIABLES;
myTextLoader.load(new URLRequest("Clothes.txt"));

myTextLoader.addEventListener(IOErrorEvent.IO_ERROR, errorload);
function errorload(e:IOErrorEvent):void {
	if (triedload==false) {
		myTextLoader.load(new URLRequest("Clothes.txt"));
		trace("secondtime");
		triedload=true;
	} else {
		trace("File not found.");
	}
}

myTextLoader.addEventListener(Event.COMPLETE, onLoaded);
function onLoaded(e:Event):void {
	numbertotal=myTextLoader.data["number"];
	txtloaded=true;
	this.addEventListener(Event.ENTER_FRAME, loadall);
	myTextLoader.removeEventListener(IOErrorEvent.IO_ERROR, errorload);
	myTextLoader.removeEventListener(Event.COMPLETE, onLoaded);
}

function loadall(e:Event):void {
	if (numbertotal==0){
		this.removeEventListener(Event.ENTER_FRAME, loadall);
		this.removeEventListener(Event.ENTER_FRAME, loading);
		gotoAndPlay(1, "New");
	}
	//Long
	if (number<=numbertotal) {
		this["load"+number] = new Loader();
		this["item"+number]=myTextLoader.data["item"+number];
		this["type"+number]=myTextLoader.data["type"+number];
		this["subtype"+number]=myTextLoader.data["subtype"+number];
		this["color"+number]=myTextLoader.data["color"+number];
		this["style"+number]=myTextLoader.data["style"+number];
		this["season"+number]=myTextLoader.data["season"+number];
		this["lastdate"+number]=myTextLoader.data["lastdate"+number];
		this["lastdateold"+number]=myTextLoader.data["lastdateold"+number];
		if (this["lastdateold"+number]==undefined) {
			this["lastdateold"+number]=myTextLoader.data["lastdate"+number];
		}
		this["deltadays"+number]=myTextLoader.data["deltadays"+number];
		this["show"+number]=myTextLoader.data["show"+number];
		this["img"+number]=myTextLoader.data["img"+number];
		if (myTextLoader.data["show"+number]==1||(myTextLoader.data["show"+number]==0&&this["editload"]!=undefined)){
			this["load"+number].load(new URLRequest(this["img"+number]));
		} else {
			this["load"+number]=undefined;
		}
		if (number<numbertotal) {
			number=number+1;
		} else {
			this.removeEventListener(Event.ENTER_FRAME, loadall);
			//Long
			/*for (number=1; number<=numbertotal; number++) {
			this["load"+number] = new Loader();
			this["item"+number]=myTextLoader.data["item"+number];
			this["type"+number]=myTextLoader.data["type"+number];
			this["subtype"+number]=myTextLoader.data["subtype"+number];
			this["color"+number]=myTextLoader.data["color"+number];
			this["style"+number]=myTextLoader.data["style"+number];
			this["season"+number]=myTextLoader.data["season"+number];
			this["lastdate"+number]=myTextLoader.data["lastdate"+number];
			this["lastdateold"+number]=myTextLoader.data["lastdateold"+number];
			if (this["lastdateold"+number]==undefined) {
			this["lastdateold"+number]=myTextLoader.data["lastdate"+number];
			}
			this["deltadays"+number]=myTextLoader.data["deltadays"+number];
			this["img"+number]=myTextLoader.data["img"+number];
			this["load"+number].load(new URLRequest(this["img"+number]));
			}
			this.removeEventListener(Event.ENTER_FRAME, loadall);*/
			//Long
			this["allitemsarray"]=[];
			this["allshowitemsarray"]=[];
			this["Tronco"]=[];
			this["Casacos"]=[];
			this["Pernas"]=[];
			this["Calçado"]=[];
			for (var i=1; i<=numbertotal; i++) {
				this["allitemsarray"].push(i);
				if (this["show"+i]==1) {
					this["allshowitemsarray"].push(i);
					if (this["subtype"+i]=="Casaco") {
						this["Casacos"].push(i);
					} else if (this["type"+i]=="Tronco") {
						this["Tronco"].push(i);
					} else if (this["type"+i]=="Pernas") {
						this["Pernas"].push(i);
					} else if (this["type"+i]=="Calçado") {
						this["Calçado"].push(i);
					}
				}
			}
			this.removeEventListener(Event.ENTER_FRAME, loading);
			if (this["editload"]==true){
				gotoAndPlay(1, "Gallery");
			} else {
				gotoAndPlay(1, "Menu");
			}
		}
	}
}

this.addEventListener(Event.ENTER_FRAME, loading);
function loading(e:Event):void {
	if (txtloaded==true) {
		bar.bar.scaleX=number/numbertotal;
		txt.text="Carregando guarda-roupa... "+Number(Math.round(number/numbertotal*100))+"%";
	}
}

