itemtxt.text="";
imgtxt.text="";
errortxt.text="";
deltadaystxt.restrict="0-9";
var numbercheck:Number=1;
var numbernew:Number=numbertotal+1;
var imglink:FileReference = new FileReference();
var imagesFilter:FileFilter=new FileFilter("Imagens","*.jpg;*.gif;*.png");

btn.addEventListener(MouseEvent.CLICK, additem);
function additem(e:Event):void {
	errortxt.text="Processando...";
	this.addEventListener(Event.ENTER_FRAME, checknew);
}
function checknew(e:Event):void {
	if (itemtxt.text!=this["item"+numbercheck]&&numbercheck<numbertotal) {
		numbercheck=(numbercheck+1);
	}
	if (itemtxt.text==""||subtypemenu.choice==""||styleslider.val.text==""||deltadaystxt.text==""||imgtxt.text=="") {
		errortxt.text="Preencha todos os campos.";
		this.removeEventListener(Event.ENTER_FRAME, checknew);
		numbercheck=1;
	}
	if (itemtxt.text==this["item"+numbercheck]) {
		errortxt.text="Item repetido...";
		this.removeEventListener(Event.ENTER_FRAME, checknew);
		numbercheck=1;
	}
	if ((itemtxt.text!=this["item"+numbercheck]&&numbercheck==numbertotal)||numbertotal==0) {
		errortxt.text="Item adicionado!";
		numbertotal=numbernew;
		this["item"+numbernew]=itemtxt.text;
		this["type"+numbernew]=typemenu.choice;
		this["subtype"+numbernew]=subtypemenu.choice;
		this["color"+numbernew]=[];
		this["color"+numbernew].push(colorcheck1.thischecked);
		this["color"+numbernew].push(colorcheck2.thischecked);
		this["color"+numbernew].push(colorcheck3.thischecked);
		this["color"+numbernew].push(colorcheck4.thischecked);
		this["style"+numbernew]=styleslider.val.text;
		this["season"+numbernew]=seasonslider.thischecked;
		this["lastdate"+numbernew]=[];
		this["lastdate"+numbernew].push(date.daynumbs);
		this["lastdate"+numbernew].push(date.monthnumbs);
		this["lastdate"+numbernew].push(date.yearnumbs);
		this["lastdateold"+numbernew]=[];
		this["lastdateold"+numbernew].push(date.daynumbs);
		this["lastdateold"+numbernew].push(date.monthnumbs);
		this["lastdateold"+numbernew].push(date.yearnumbs);
		this["deltadays"+numbernew]=deltadaystxt.text;
		this["show"+numbernew]=1;
		this["img"+numbernew]=imgtxt.text;
		trace("Item "+numbernew);
		trace(this["item"+numbernew]);
		trace(this["type"+numbernew]);
		trace(this["subtype"+numbernew]);
		trace(this["color"+numbernew]);
		trace((this["style"+numbernew])+"%");
		trace(this["season"+numbernew]);
		trace(this["lastdate"+numbernew]);
		trace(this["lastdateold"+numbernew]);
		trace((this["deltadays"+numbernew])+" dias");
		trace(this["show"+numbernew]);
		trace(this["img"+numbernew]);
		this.removeEventListener(Event.ENTER_FRAME, checknew);
		this.removeEventListener(Event.ENTER_FRAME, givelabels);
		loadbtn.removeEventListener(MouseEvent.CLICK, loadIt);
		imglink.removeEventListener(Event.SELECT, selectImage);
		btn.removeEventListener(MouseEvent.CLICK, additem);
		numbercheck=1;
		setTimeout(wait2,1000);
		function wait2() {
			gotoAndStop(1, "Save");
		}
	}
}

loadbtn.addEventListener(MouseEvent.CLICK, loadIt);
function loadIt(e:Event):void {
	imglink.browse([imagesFilter]);
}

imglink.addEventListener(Event.SELECT, selectImage);
function selectImage(e:Event):void {
	var extensionIndex:Number=imglink.name.lastIndexOf('.');
	var extension:String=imglink.name.substr(extensionIndex,5);
	trace(imglink.name);
	if (extension==".jpeg"||extension==".jpg"||extension==".png") {
		imgtxt.text=(imglink.name);
	} else {
		imgtxt.text="";
		errortxt.text="Selecione uma imagem válida.";
	}
}

this.addEventListener(Event.ENTER_FRAME, givelabels);
function givelabels(e:Event):void {
	colorcheck1.thislabel="Azul";
	colorcheck2.thislabel="Castanho";
	colorcheck3.thislabel="Escuro";
	colorcheck4.thislabel="Claro";
	typemenu.labels=["Tronco","Pernas","Calçado"];
	if (typemenu.choice=="Tronco") {
		subtypemenu.labels=["Camisola","Sweatshirt","T-shirt","Camisa","Polo","Malha","Casaco"];
	}
	if (typemenu.choice=="Pernas") {
		subtypemenu.labels=["Calças","Calções","Calções de Banho"];
	}
	if (typemenu.choice=="Calçado") {
		subtypemenu.labels=["Sapatos","Sapatilhas","Botas","Chinelos"];
	}
}

backbtn.addEventListener(MouseEvent.CLICK, returnnew);
function returnnew(e:Event):void {
	this.removeEventListener(Event.ENTER_FRAME, checknew);
	this.removeEventListener(Event.ENTER_FRAME, givelabels);
	loadbtn.removeEventListener(MouseEvent.CLICK, loadIt);
	imglink.removeEventListener(Event.SELECT, selectImage);
	btn.removeEventListener(MouseEvent.CLICK, additem);
	backbtn.removeEventListener(MouseEvent.CLICK, returnnew);
	gotoAndStop(1, "Menu");
}

