this.addEventListener(Event.ENTER_FRAME, itemnumber);
function itemnumber(e:Event):void {
	if (no.text=="") {
		errortxt.text="Insira um número válido.";
		validnumber=false;
		delbtn.visible=false;
		imgedit.number=0;
		imgeditnumber=0;
	} else if (numberactual!=no.text) {
		errortxt.text="";
		numberactual=no.text;
		imgedit.number=0;
		imgeditnumber=0;
		if (numberactual>numbertotal||numberactual<=0) {
			errortxt.text="Insira um número entre 1 e "+numbertotal+".";
			itemtxt.text="";
			delbtn.visible=false;
			typemenu.choicedefined=true;
			typemenu.choice="Tronco";
			subtypemenu.choicedefined=true;
			subtypemenu.choice="Camisola";
			colorcheck1.thischecked=0;
			colorcheck1.justselected=true;
			colorcheck2.thischecked=0;
			colorcheck2.justselected=true;
			colorcheck3.thischecked=0;
			colorcheck3.justselected=true;
			colorcheck4.thischecked=0;
			colorcheck4.justselected=true;
			styleslider.val.text="";
			seasonslider.thischecked=0;
			seasonslider.justselected=true;
			date.daynumbs="1";
			date.monthnumbs="0";
			date.yearnumbs="2016";
			deltadaystxt.text="";
			showcheck.thischecked=1;
			showcheck.justselected=true;
			imgtxt.text="";
			validnumber=false;
			bare.width=0;
			bare.alpha=0;
		} else if (numberactual<=numbertotal) {
			for (var i:Number=0; itemsarray[i]<=numbertotal; i++) {
				if (itemsarray[numbpos]==numberactual) {
					break;
				}
				if (itemsarray[i]==numberactual) {
					numbpos=i;
					break;
				}
			}
			delbtn.visible=false;
			if (numberactual==numbertotal){
				delbtn.visible=true;
			}
			bare.width=0;
			bare.alpha=0;
			errortxt.text="";
			itemtxt.text=this["item"+numberactual];
			date.waitingforchoice=true;
			if (this["lastdate"+numberactual]!="undefined") {
				if (date.waitingforchoice==true) {
					date.daynumbs=Number(this["lastdate"+numberactual][0]);
					date.monthnumbs=Number(this["lastdate"+numberactual][1]);
					date.yearnumbs=Number(this["lastdate"+numberactual][2]);
					date.waitingforchoice=false;
				}
			} else {
				var dateactual:Date=new Date();
				date.daynumbs=1;//dateactual.getDate();
				date.monthnumbs=0;//dateactual.getMonth();
				date.yearnumbs=dateactual.getFullYear();
				date.waitingforchoice=false;
			}
			typemenu.choicedefined=false;
			subtypemenu.choicedefined=false;
			if (this["type"+numberactual]!="undefined") {
				typemenu.choicedefined=true;
				typemenu.choice=this["type"+numberactual];
			}
			if (this["subtype"+numberactual]!="undefined") {
				subtypemenu.choicedefined=true;
				subtypemenu.choice=this["subtype"+numberactual];
				styleslider.val.text=this["style"+numberactual];
				deltadaystxt.text=this["deltadays"+numberactual];
			}
			if (this["color"+numberactual]!="undefined") {
				colorcheck1.thischecked=this["color"+numberactual][0];
				colorcheck1.justselected=true;
				colorcheck2.thischecked=this["color"+numberactual][1];
				colorcheck2.justselected=true;
				colorcheck3.thischecked=this["color"+numberactual][2];
				colorcheck3.justselected=true;
				colorcheck4.thischecked=this["color"+numberactual][3];
				colorcheck4.justselected=true;
			} else {
				this["color"+numberactual]=[0,0,0,0];
				colorcheck1.thischecked=this["color"+numberactual][0];
				colorcheck1.justselected=true;
				colorcheck2.thischecked=this["color"+numberactual][1];
				colorcheck2.justselected=true;
				colorcheck3.thischecked=this["color"+numberactual][2];
				colorcheck3.justselected=true;
				colorcheck4.thischecked=this["color"+numberactual][3];
				colorcheck4.justselected=true;
			}
			if (this["season"+numberactual]!="undefined") {
				seasonslider.thischecked=this["season"+numberactual];
				seasonslider.justselected=true;
			} else {
				seasonslider.thischecked=0;
				seasonslider.justselected=true;
			}
			if (this["show"+number]!="undefined") {
				showcheck.thischecked=this["show"+numberactual];
				showcheck.justselected=true;
			} else {
				this["show"+number]=1;
				showcheck.justselected=true;
			}
			imgtxt.text=this["img"+numberactual];
			if (this["load"+numberactual]!=undefined){
				imgeditnumber=numberactual;
			}
			validnumber=true;
		}
	}
}

var imglinko:FileReference = new FileReference();
var imagesFiltero:FileFilter=new FileFilter("Imagens","*.jpg;*.gif;*.png");

btn.addEventListener(MouseEvent.CLICK, submititem);
function submititem(e:Event):void {
	if (validnumber==true) {
		errortxt.text="Processando...";
		notext=no.text
		this.addEventListener(Event.ENTER_FRAME, checkname);
	} else {
		errortxt.text="Item não encontrado.";
	}
}

function checkname(e:Event):void {
	if (numbercheck<numbertotal && (numbercheck==numberactual || itemtxt.text!=this["item"+numbercheck])) {//Se o nome for diferente
		if (itemtxt.text==this["item"+numberactual]){
			numbercheck=numbertotal-1;
		}
		trace("Checking "+numbercheck+" de "+numbertotal);
		bare.width=237.95-((numbercheck*237.95)/numbertotal);
		bare.alpha=1-(numbercheck/numbertotal);
		onverification=true;
		if (no.text!=notext){
			no.text=notext;
		}
		numbercheck=(numbercheck+1);
	}
	if (itemtxt.text==""||subtypemenu.choice==""||styleslider.val.text==""||deltadaystxt.text==""||imgtxt.text=="") {//Se algum campo estiver em branco
		errortxt.text="Preencha todos os campos.";
		this.removeEventListener(Event.ENTER_FRAME, checkname);
		numbercheck=1;
	}
	if (itemtxt.text==this["item"+numbercheck]&&itemtxt.text!=this["item"+numberactual]) {//Se estiver repetido
		errortxt.text="Item repetido...";
		trace("Checking "+numbercheck+" de "+numbertotal);
		this.removeEventListener(Event.ENTER_FRAME, checkname);
		onverification=false;
		numbercheck=1;
	}
	if (numbercheck==numbertotal && (itemtxt.text!=this["item"+numbercheck] || numbercheck==numberactual)) {//Se chegar ao final e não estiver repetido
		bare.width=0;
		bare.alpha=0;
		trace("Checking "+numbercheck+" de "+numbertotal);
		if (this["show"+numberactual]!=showcheck.thischecked&&showcheck.thischecked==1&&this["editload"]==undefined){
			backbtn.visible=false;
			}
		this["item"+numberactual]=itemtxt.text;
		this["type"+numberactual]=typemenu.choice;
		this["subtype"+numberactual]=subtypemenu.choice;
		this["color"+numberactual]=[];
		this["color"+numberactual].push(colorcheck1.thischecked);
		this["color"+numberactual].push(colorcheck2.thischecked);
		this["color"+numberactual].push(colorcheck3.thischecked);
		this["color"+numberactual].push(colorcheck4.thischecked);
		this["style"+numberactual]=styleslider.val.text;
		this["season"+numberactual]=seasonslider.thischecked;
		if (this["lastdate"+numberactual][0]!=date.daynumbs||this["lastdate"+numberactual][1]!=date.monthnumbs||this["lastdate"+numberactual][2]!=date.yearnumbs) {
			this["lastdateold"+numberactual]=[];
			this["lastdateold"+numberactual].push(this["lastdate"+numberactual][0]);
			this["lastdateold"+numberactual].push(this["lastdate"+numberactual][1]);
			this["lastdateold"+numberactual].push(this["lastdate"+numberactual][2]);
		}
		this["lastdate"+numberactual]=[];
		this["lastdate"+numberactual].push(date.daynumbs);
		this["lastdate"+numberactual].push(date.monthnumbs);
		this["lastdate"+numberactual].push(date.yearnumbs);
		this["deltadays"+numberactual]=deltadaystxt.text;
		this["show"+numberactual]=showcheck.thischecked;
		this["img"+numberactual]=imgtxt.text;
		errortxt.text="Item editado!";
		trace("Item "+numberactual);
		trace(this["item"+numberactual]);
		trace(this["type"+numberactual]);
		trace(this["subtype"+numberactual]);
		trace(this["color"+numberactual]);
		trace((this["style"+numberactual])+"%");
		trace(this["season"+numberactual]);
		trace(this["lastdate"+numberactual]);
		trace((this["deltadays"+numberactual])+" dias");
		trace(this["show"+numberactual]);
		trace(this["img"+numberactual]);
		onverification=false;
		notsaved.visible=true;
		changesnotsaved=true;
		numbercheck=1;
		this.removeEventListener(Event.ENTER_FRAME, checkname);
	}
}

notsaved.btn.addEventListener(MouseEvent.CLICK, savefinal);
function savefinal(e:Event):void {
	if (onverification==false) {
		this.removeEventListener(Event.ENTER_FRAME, itemnumber);
		btn.removeEventListener(MouseEvent.CLICK, submititem);
		loadbtn.removeEventListener(MouseEvent.CLICK, loadIti);
		imglinko.removeEventListener(Event.SELECT, selectImagee);
		this.removeEventListener(Event.ENTER_FRAME, givelabelsedit);
		notsaved.btn.removeEventListener(MouseEvent.CLICK, savefinal);
		numbercheck=1;
		setTimeout(wait2,1000);
		function wait2() {
			gotoAndStop(1, "Save");
		}
	}
}

loadbtn.addEventListener(MouseEvent.CLICK, loadIti);
function loadIti(e:Event):void {
	imglinko.browse([imagesFiltero]);
}

imglinko.addEventListener(Event.SELECT, selectImagee);
function selectImagee(e:Event):void {
	var extensionIndex:Number=imglinko.name.lastIndexOf('.');
	var extension:String=imglinko.name.substr(extensionIndex,5);
	trace(imglinko.name);
	if (extension==".jpeg"||extension==".jpg"||extension==".png") {
		imgtxt.text=(imglinko.name);
	} else {
		imgtxt.text="";
		errortxt.text="Selecione uma imagem válida.";
	}
}

this.addEventListener(Event.ENTER_FRAME, givelabelsedit);
function givelabelsedit(e:Event):void {
	colorcheck1.thislabel="Azul";
	colorcheck2.thislabel="Castanho";
	colorcheck3.thislabel="Escuro";
	colorcheck4.thislabel="Claro";
	showcheck.txtlabel.visible=false;
	typemenu.labels=["Tronco","Pernas","Calçado"];
	if (typemenu.choice=="Tronco") {
		subtypemenu.labels=["Camisola","Sweatshirt","T-shirt","Camisa","Polo","Malha","Casaco"];
	}
	if (typemenu.choice=="Pernas") {
		subtypemenu.labels=["Calças","Calções","Calções de Banho"];
	}
	if (typemenu.choice=="Calçado") {
		subtypemenu.labels=["Sapatos","Sapatilhas","Botas","Chinelos"];
	}
	if (aw.leftpressed==true&&onverification==false) {
		aw.leftpressed=false;
		if (numbpos>0) {
			numbpos=numbpos-1;
			no.text=itemsarray[numbpos];
		}
	}
	if (aw.rightpressed==true&&onverification==false) {
		aw.rightpressed=false;
		if (numbpos<itemsarray.length-1) {
			numbpos=numbpos+1;
			no.text=itemsarray[numbpos];
		}
	}
	if (imgeditnumber!=0 && imgedit.awaiting==true){
		imgedit.number=imgeditnumber;
	}
}

function wait3() {
	if (showcheck.waitingforchoice==true) {
		showcheck.thischecked=this["show"+numberactual];
		showcheck.justselected=true;
	}
	if (colorcheck4.waitingforchoice==true) {
		colorcheck1.thischecked=this["color"+numberactual][0];
		colorcheck1.justselected=true;
		colorcheck2.thischecked=this["color"+numberactual][1];
		colorcheck2.justselected=true;
		colorcheck3.thischecked=this["color"+numberactual][2];
		colorcheck3.justselected=true;
		colorcheck4.thischecked=this["color"+numberactual][3];
		colorcheck4.justselected=true;
	}
	typemenu.choice=this["type"+numberactual];
	typemenu.choicedefined=true;
	subtypemenu.choice=this["subtype"+numberactual];
	subtypemenu.choicedefined=true;
	seasonslider.thischecked=this["season"+numberactual];
	seasonslider.justselected=true;
	if (this["lastdate"+numberactual]!="undefined") {
		if (date.waitingforchoice==true) {
			date.daynumbs=Number(this["lastdate"+numberactual][0]);
			date.monthnumbs=Number(this["lastdate"+numberactual][1]);
			date.yearnumbs=Number(this["lastdate"+numberactual][2]);
			date.waitingforchoice=false;
		}
	} else {
		var dateactual:Date=new Date();
		//date.daynumbs=dateactual.getDate();
		//date.monthnumbs=dateactual.getMonth();
		date.yearnumbs=dateactual.getFullYear();
		date.waitingforchoice=false;
	}
	if (this["load"+numberactual]!=undefined){
			imgeditnumber=numberactual;
		}
}

function whichlist(e:Event):void {
	if (this["GalActualArray"]=="Casaco") {
		itemsarray=this["Casacos"];
	} else if (this["GalActualArray"]=="Tronco") {
		itemsarray=this["Tronco"];
	} else if (this["GalActualArray"]=="Pernas") {
		itemsarray=this["Pernas"];
	} else if (this["GalActualArray"]=="Calçado") {
		itemsarray=this["Calçado"];
	} else {
		itemsarray=this["allitemsarray"];
		this.removeEventListener(Event.ENTER_FRAME, whichlist);
	}
	for (var i:Number=0; itemsarray[i]<=number; i++) {
		if (itemsarray[i]==number) {
			numbpos=i;
		}
	}
	this.removeEventListener(Event.ENTER_FRAME, whichlist);
}

