this.addEventListener(Event.ENTER_FRAME, givepage);
function givepage(e:Event):void {
	pagetxt.text=gal.page;
	if (gal.itemsarray=="") {
		gal.itemsarray=itemsarray;
	}
	if (aw.leftpressed==true&&onverificgal==false) {
		aw.leftpressed=false;
		if (pagen>1&&gal.endedpage==true) {
			gal.endedpage=false;
			pagen=pagen-1;
		}
	}
	if (aw.rightpressed==true&&onverificgal==false) {
		aw.rightpressed=false;
		if (pagen<totalpage&&gal.endedpage==true) {
			gal.endedpage=false;
			pagen=pagen+1;
		}
	}
	if (gal.page!=pagen) {
		gal.page=pagen;
		gal.pagechanged=true;
		gal.mini1.number=0;
		gal.mini2.number=0;
		gal.mini3.number=0;
		gal.mini4.number=0;
		gal.mini5.number=0;
		gal.mini6.number=0;
		gal.mini7.number=0;
		gal.mini8.number=0;
		gal.mini9.number=0;
		gal.mini10.number=0;
		gal.mini11.number=0;
		gal.mini12.number=0;
	}
	if (gal.thingselected!=0&&gal.thingselected!="back"&&gal.thingselected!="goonedit") {
		this.removeEventListener(Event.ENTER_FRAME, givepage);
		numberselected=gal.thingselected;
		if (gal.mini1.out=="check"||gal.mini2.out=="check"||gal.mini3.out=="check"||gal.mini4.out=="check"||gal.mini5.out=="check"||gal.mini6.out=="check"||gal.mini7.out=="check"||gal.mini8.out=="check"||gal.mini9.out=="check"||gal.mini10.out=="check"||gal.mini11.out=="check"||gal.mini12.out=="check") {
			if (this["CombBackup"]!=undefined) {
				gotoAndStop(1, "Combine");
			} else {
				gotoAndStop(1, "Edit");
			}
		}
	}
	if (closing==true) {
		if (gal.mini1.out=="check"||gal.mini2.out=="check"||gal.mini3.out=="check"||gal.mini4.out=="check"||gal.mini5.out=="check"||gal.mini6.out=="check"||gal.mini7.out=="check"||gal.mini8.out=="check"||gal.mini9.out=="check"||gal.mini10.out=="check"||gal.mini11.out=="check"||gal.mini12.out=="check") {
			this.removeEventListener(Event.ENTER_FRAME, givepage);
			if (this["CombBackup"]!=undefined) {
				numberselected=394;
				gotoAndStop(1, "Combine");
			} else if (this["editload"]==true&&gal.thingselected=="goonedit") {
				gotoAndStop(1, "Loader");
			} else {
				gotoAndStop(1, "Menu");
			}
		}
	}
}

