function tosavegal(e:Event):void {
	if (numbergal<=numbertotal&&stageofconvert==1) {
		trace(numbergal, (numbergal+numbertotal));
		this["item"+(numbergal+numbertotal)]=this["item"+numbergal];
		this["type"+(numbergal+numbertotal)]=this["type"+numbergal];
		this["subtype"+(numbergal+numbertotal)]=this["subtype"+numbergal];
		this["color"+(numbergal+numbertotal)]=this["color"+numbergal];
		this["style"+(numbergal+numbertotal)]=this["style"+numbergal];
		this["season"+(numbergal+numbertotal)]=this["season"+numbergal];
		this["lastdate"+(numbergal+numbertotal)]=this["lastdate"+numbergal];
		this["lastdateold"+(numbergal+numbertotal)]=this["lastdateold"+numbergal];
		this["deltadays"+(numbergal+numbertotal)]=this["deltadays"+numbergal];
		this["show"+(numbergal+numbertotal)]=this["show"+numbergal];
		this["img"+(numbergal+numbertotal)]=this["img"+numbergal];
		this["load"+(numbergal+numbertotal)]=this["load"+numbergal];
		baredit.height=166-((numbergal*166)/(numbertotal*2));
		baredit.alpha=1-(numbergal/(numbertotal*2));
		if (numbergal==numbertotal) {
			numbergal=1;
			stageofconvert=2;
		} else {
			numbergal+=1;
		}
	} else if (pconvgal<=totalpage && stageofconvert==2) {
		if (numbergal<=12&&(numbergal+12*(pconvgal-1))<=numbertotal) {
			var numbconvgal1=numbergal+12*(pconvgal-1);
			var numbconvgal2=gal["tini"+numbconvgal1]+numbertotal;
			trace(numbconvgal1,numbconvgal2,this["item"+numbconvgal1],this["item"+numbconvgal2]);
			this["item"+numbconvgal1]=this["item"+numbconvgal2];
			this["type"+numbconvgal1]=this["type"+numbconvgal2];
			this["subtype"+numbconvgal1]=this["subtype"+numbconvgal2];
			this["color"+numbconvgal1]=this["color"+numbconvgal2];
			this["style"+numbconvgal1]=this["style"+numbconvgal2];
			this["season"+numbconvgal1]=this["season"+numbconvgal2];
			this["lastdate"+numbconvgal1]=this["lastdate"+numbconvgal2];
			this["lastdateold"+numbconvgal1]=this["lastdateold"+numbconvgal2];
			this["deltadays"+numbconvgal1]=this["deltadays"+numbconvgal2];
			this["show"+numbconvgal1]=this["show"+numbconvgal2];
			this["img"+numbconvgal1]=this["img"+numbconvgal2];
			this["load"+numbconvgal1]=this["load"+numbconvgal2];
			this["item"+numbconvgal2]=[];
			this["type"+numbconvgal2]=[];
			this["subtype"+numbconvgal2]=[];
			this["color"+numbconvgal2]=[];
			this["style"+numbconvgal2]=[];
			this["season"+numbconvgal2]=[];
			this["lastdate"+numbconvgal2]=[];
			this["lastdateold"+numbconvgal2]=[];
			this["deltadays"+numbconvgal2]=[];
			this["show"+numbconvgal2]=[];
			this["img"+numbconvgal2]=[];
			this["load"+numbconvgal2]=[];
			baredit.height=(166/2)-(((numbergal+12*(pconvgal-1))*166)/(numbertotal*2));
			baredit.alpha=(1/2)-((numbergal+12*(pconvgal-1))/(numbertotal*2));
			if (numbergal==12) {
				numbergal=1;
				pconvgal+=1;
			} else {
				numbergal+=1;
			}
		} else {
			this.removeEventListener(Event.ENTER_FRAME, tosavegal);
			pconvgal=1;
			numbergal=1;
			stageofconvert=1;
			onverificgal=false;
			notsavedbtn.visible=true;
			changesnotsaved=true;
		}
	}
}

function orderthis(arr:Array):Array {
	var datesorder:Array=[];
	var disdatesorder:Array=[];
	var ordere:Array=[];
	for (var ai=0; ai<arr.length; ai++) {
		var datai:Date=new Date(this["lastdate"+arr[ai]][2],this["lastdate"+arr[ai]][1],this["lastdate"+arr[ai]][0]);
		datesorder.push(datai.getTime());
		disdatesorder.push(datai.getTime());
	}
	datesorder.sort(Array.NUMERIC);
	for (var aii=0; aii<arr.length; aii++) {
		var ind=disdatesorder.indexOf(datesorder[aii]);
		disdatesorder.splice(ind,1,"n");
		ordere.push(arr[ind]);
	}
	ordere.reverse();
	return ordere;
}

function subtractarr(arr1:Array,arr2:Array):Array {
	var finalarr:Array = [];
	for (var lar=0; lar<arr1.length; lar++) {
		if (arr2.indexOf(arr1[lar])==-1) {
			finalarr.push(arr1[lar])
		}
	}
	return finalarr;
}

