	}
}

function loadalle(e:Event):void {
	for (var n=1; n<=numbertotal; n++) {
		if (this["show"+n]==1) {
			if ((this["subtype"+n]=="Casaco"&&holdenumber==-1)||(this["type"+n]=="Tronco"&&this["subtype"+n]!="Casaco"&&holdtnumber==-1)||(this["type"+n]=="Pernas"&&holdpnumber==-1)||(this["type"+n]=="Calçado"&&holdcnumber==-1)) {
				if (this["lastdate"+n]!="undefined") {
					var datethis:Date=new Date(this["lastdate"+n][2],this["lastdate"+n][1],this["lastdate"+n][0]);
				} else {
					datethis=dateundefined;
				}
				dateinterval=Math.abs(datethis.getTime()-dateactual.getTime())/(1000*60*60*24);
				//trace(this["item"+number],dateinterval,this["deltadays"+number])
				if (((this["style"+n]>=slider.perc-40&&this["style"+n]<=slider.perc+40)||slider.perc==50)&&dateinterval>=this["deltadays"+n]&&(seasonval==0||this["season"+n]==0 || this["season"+n]==seasonval)) {
					if (((colorselect[0]==1 && this["color"+n][0]==1)||(colorselect[0]=="*" && (this["color"+n][0]==0||this["color"+n][0]==1)))&&
					((colorselect[1]==1 && this["color"+n][1]==1)||(colorselect[1]=="*" && (this["color"+n][1]==0||this["color"+n][1]==1)))&&
					((colorselect[2]==1 && this["color"+n][2]==1)||(colorselect[2]=="*" && (this["color"+n][2]==0||this["color"+n][2]==1)))&&
					((colorselect[3]==1 && this["color"+n][3]==1)||(colorselect[3]=="*" && (this["color"+n][3]==0||this["color"+n][3]==1)))) {
						if (this["subtype"+n]==typee4) {
							casaco.push(n);
						} else if (this["type"+n]==typee1) {
							tronco.push(n);
						} else if (this["type"+n]==typee2) {
							pernas.push(n);
						} else if (this["type"+n]==typee3) {
							calçado.push(n);
						}
					}
				}
			}
		}
	}
	this.removeEventListener(Event.ENTER_FRAME, loadalle);
	this.addEventListener(Event.ENTER_FRAME, loadallew);
}

function loadallew(e:Event):void {
	if (eloader.awaiting==true&&tloader.awaiting==true&&ploader.awaiting==true&&cloader.awaiting==true) {
		if (holdenumber!=-1) {
			casaco.push(holdenumber);
		}
		if (holdtnumber!=-1) {
			tronco.push(holdtnumber);
		}
		if (holdpnumber!=-1) {
			pernas.push(holdpnumber);
		}
		if (holdcnumber!=-1) {
			calçado.push(holdcnumber);
		}
		if (tronco.length!=0&&pernas.length!=0&&calçado.length!=0) {
			//trace(calçado[int(Math.random()*calçado.length)]);
			tloader.number=(tronco[int(Math.random()*tronco.length)]);
			trace(this["item"+tloader.number]+" "+ tloader.number);
			ploader.number=(pernas[int(Math.random()*pernas.length)]);
			trace(this["item"+ploader.number]+" "+ ploader.number);
			cloader.number=(calçado[int(Math.random()*calçado.length)]);
			trace(this["item"+cloader.number]+" "+ cloader.number);
			eloader.number=(casaco[int(Math.random()*casaco.length)]);
			trace(this["item"+eloader.number]+" "+ eloader.number);
		} else {
			trace("Not enought");
		}
		this.removeEventListener(Event.ENTER_FRAME, loadallew);
		trace("Itens de tronco "+tronco.toString());
		trace("Itens de pernas "+pernas.toString());
		trace("Itens de calçado "+calçado.toString());
		trace("Itens de casaco "+casaco.toString());
		btn.addEventListener(MouseEvent.CLICK, generatecomb);
	}
}

//trace(this["item"+(randomRange(1, numbertotal))]);
//trace(this["item"+(randomRange(1, numbertotal))]);

function randomRange(minNum:Number, maxNum:Number):Number {
	return (Math.floor(Math.random() * (maxNum - minNum + 1)) + minNum);
}

this.addEventListener(Event.ENTER_FRAME, givelabelscomb);
function givelabelscomb(e:Event):void {
	check1.thislabel="Azul";
	check2.thislabel="Castanho";
	check3.thislabel="Escuro";
	check4.thislabel="Claro";
	checkrandom.thislabel="Aleatório";
	if ((check1.thischecked==1||check2.thischecked==1||check3.thischecked==1||check4.thischecked==1)&&(checkrandom.justselected==true&&checkrandom.thischecked==1)) {
		check1.thischecked=0;
		check2.thischecked=0;
		check3.thischecked=0;
		check4.thischecked=0;
		check1.justselected=true;
		check2.justselected=true;
		check3.justselected=true;
		check4.justselected=true;
	}
	if (((check1.thischecked==1&&check1.justselected==true)||(check2.thischecked==1&&check2.justselected==true)||(check3.thischecked==1&&check3.justselected==true)||(check4.thischecked==1&&check4.justselected==true))&&checkrandom.thischecked==1) {
		checkrandom.justselected=true;
		checkrandom.thischecked=0;
	}
	if (date.waitingforchoice==true) {
		date.daynumbs=dateactuale.date;
		date.monthnumbs=dateactuale.month;
		date.yearnumbs=dateactuale.fullYear;
		date.waitingforchoice=false;
	}
	if (this["CombBackup"]!=undefined&&checkrandom.waitingforchoice==true) {
		date.daynumbs=this["CombBackup"][0];
		date.monthnumbs=this["CombBackup"][1];
		date.yearnumbs=this["CombBackup"][2];
		datechack=[date.daynumbs,date.monthnumbs,date.yearnumbs];
		slider.val.text=this["CombBackup"][3];
		check1.thischecked=this["CombBackup"][4];
		check2.thischecked=this["CombBackup"][5];
		check3.thischecked=this["CombBackup"][6];
		check4.thischecked=this["CombBackup"][7];
		checkrandom.thischecked=this["CombBackup"][8];
		seasonslider.thischecked=this["CombBackup"][9];
		tloader.number=this["CombBackup"][10];
		ploader.number=this["CombBackup"][11];
		cloader.number=this["CombBackup"][12];
		eloader.number=this["CombBackup"][13];
		sloader.number=this["CombBackup"][19];
		if (this["CombBackup"][14]=="tloader"&&numberselected!=394) {
			tloader.number=numberselected;
		} else if (this["CombBackup"][14]=="ploader"&&numberselected!=394) {
			ploader.number=numberselected;
		} else if (this["CombBackup"][14]=="cloader"&&numberselected!=394) {
			cloader.number=numberselected;
		} else if (this["CombBackup"][14]=="eloader"&&numberselected!=394) {
			eloader.number=numberselected;
		} else if (this["CombBackup"][14]=="sloader"&&numberselected!=394) {
			sloader.number=numberselected;
		}
		thold.thisselected=this["CombBackup"][15];
		phold.thisselected=this["CombBackup"][16];
		chold.thisselected=this["CombBackup"][17];
		ehold.thisselected=this["CombBackup"][18];
		shold.thisselected=this["CombBackup"][20];
		if (this["CombBackup"][21]==true) {
			expandmode=false;
			expandjustsel=true;
			expnumb=6;
		}
		this["CombBackup"]=undefined;
		numberselected=0;
		checkrandom.waitingforchoice=false;
	}
	if ((datechack[0]!=date.daynumbs||datechack[1]!=date.monthnumbs||datechack[2]!=date.yearnumbs)) {
		tloader.number=null;
		ploader.number=null;
		cloader.number=null;
		eloader.number=null;
		if (tloader.awaiting==true&&ploader.awaiting==true&&cloader.awaiting==true&&eloader.awaiting==true) {
			var dateactual1=new Date(date.yearnumbs,date.monthnumbs,date.daynumbs);
			var dateactual1Timestamp:Number=dateactual1.getTime();
			this["outoftheday"]=[0,0,0,0];
			for (var i=1; i<=numbertotal; i++) {
				if (this["show"+i]==1||(this["show"+i]==0&&this["editload"]!=undefined)) {
					var datethis:Date=new Date(this["lastdate"+i][2],this["lastdate"+i][1],this["lastdate"+i][0]);
					var datethisTimestamp:Number=datethis.getTime();
					var datethisold:Date=new Date(this["lastdateold"+i][2],this["lastdateold"+i][1],this["lastdateold"+i][0]);
					var datethisoldTimestamp:Number=datethisold.getTime();
					if (datethisTimestamp==dateactual1Timestamp||datethisoldTimestamp==dateactual1Timestamp) {
						if (this["subtype"+i]=="Casaco") {
							this["outoftheday"][3]=i;
						} else if (this["type"+i]=="Tronco") {
							this["outoftheday"][0]=i;
						} else if (this["type"+i]=="Pernas") {
							this["outoftheday"][1]=i;
						} else if (this["type"+i]=="Calçado") {
							this["outoftheday"][2]=i;
						}
					}
				}
			}
			tloader.number=this["outoftheday"][0];
			ploader.number=this["outoftheday"][1];
			cloader.number=this["outoftheday"][2];
			eloader.number=this["outoftheday"][3];
			datechack=[date.daynumbs,date.monthnumbs,date.yearnumbs];
		}
	}
	if (tloader.number!=0) {
		tblank.visible=true;
	} else {
		tblank.visible=false;
	}
	if (ploader.number!=0) {
		pblank.visible=true;
	} else {
		pblank.visible=false;
	}
}

edite.addEventListener(MouseEvent.CLICK, editesquares);
function editesquares(e:Event):void {
	if (editmode==false) {
		tloader.thisselected=false;
		ploader.thisselected=false;
		cloader.thisselected=false;
		eloader.thisselected=false;
		if (expandmode==true) {
			sloader.thisselected=false;
			sloader.editablee=true;
		}
		tloader.editablee=true;
		ploader.editablee=true;
		cloader.editablee=true;
		eloader.editablee=true;
		exp.visible=true;
		editmode=true;
		this.addEventListener(Event.ENTER_FRAME, editestuff);
	} else if (editmode==true) {
		tloader.editablee=false;
		ploader.editablee=false;
		cloader.editablee=false;
		eloader.editablee=false;
		if (expandmode==true) {
			sloader.editablee=false;
		}
		exp.visible=false;
		editmode=false;
		this.removeEventListener(Event.ENTER_FRAME, editestuff);
	}
}

