exp.addEventListener(MouseEvent.CLICK, expandbtn);
function expandbtn(e:MouseEvent):void {
	if (expandmode==false) {
		expandjustsel=true;
	} else if (expandmode==true) {
		expandjustsel=true;
	}
}

addEventListener(Event.ENTER_FRAME, expandfunct);
function expandfunct(e:Event):void {
	if (expandjustsel==true) {
		if (expandmode==false) {//Opening
			if (expnumb>=6) {
				expandjustsel=false;
				shold.visible=true;
				sloader.visible=true;
				if (editmode==true) {
					sloader.thisselected=false;
					sloader.editablee=true;
				}
				ehold.y=206.95;
				eloader.y=136.6;
				exp.gotoAndStop(2);
				exp.y=279.4;
				expandmode=true;
			} else {
				expnumb+=1;
				eloader.y=81+((expnumb*expnumb*55.6)/36);
				ehold.y=151.35+((expnumb*expnumb*55.6)/36);
				exp.y=223.8+((expnumb*expnumb*55.6)/36);
			}
		} else if (expandmode==true) {//Closing
			shold.visible=false;
			sloader.visible=false;
			sloader.editablee=false;
			if (expnumb<=0) {
				expandjustsel=false;
				ehold.y=151.35;
				eloader.y=81;
				exp.gotoAndStop(1);
				exp.y=223.8;
				expnumb=0;
				expandmode=false;
			} else {
				expnumb-=1;
				eloader.y=81+((expnumb*expnumb*55.6)/36);
				ehold.y=151.35+((expnumb*expnumb*55.6)/36);
				exp.y=223.8+((expnumb*expnumb*55.6)/36);
			}
		}
	}
}

checkinbtn.addEventListener(MouseEvent.CLICK, checkin);
function checkin(e:Event):void {
	var inicials:Array=["t","p","c","e"];
	for (var i=0; i<4; i++) {
		if (this[inicials[i]+"loader"].number!=0&&this[inicials[i]+"loader"].number!=undefined) {
			if (this["lastdate"+this[inicials[i]+"loader"].number][0]!=date.daynumbs||this["lastdate"+this[inicials[i]+"loader"].number][1]!=date.monthnumbs||this["lastdate"+this[inicials[i]+"loader"].number][2]!=date.yearnumbs) {
				this["lastdateold"+this[inicials[i]+"loader"].number]=[];
				this["lastdateold"+this[inicials[i]+"loader"].number].push(this["lastdate"+this[inicials[i]+"loader"].number][0]);
				this["lastdateold"+this[inicials[i]+"loader"].number].push(this["lastdate"+this[inicials[i]+"loader"].number][1]);
				this["lastdateold"+this[inicials[i]+"loader"].number].push(this["lastdate"+this[inicials[i]+"loader"].number][2]);
				this["lastdate"+this[inicials[i]+"loader"].number]=[];
				this["lastdate"+this[inicials[i]+"loader"].number].push(date.daynumbs);
				this["lastdate"+this[inicials[i]+"loader"].number].push(date.monthnumbs);
				this["lastdate"+this[inicials[i]+"loader"].number].push(date.yearnumbs);
				notsaved.visible=true;
				changesnotsaved=true;
			}
		}
	}
}

