	ploader.out=true;
	cloader.out=true;
	eloader.out=true;
	notsaved.btn.removeEventListener(MouseEvent.CLICK, savefinale);
	checkinbtn.removeEventListener(MouseEvent.CLICK, checkin);
	edite.removeEventListener(MouseEvent.CLICK, editesquares);
	this.removeEventListener(Event.ENTER_FRAME, givelabelscomb);
	this.removeEventListener(Event.ENTER_FRAME, editestuff);
	this.removeEventListener(Event.ENTER_FRAME, loadalle);
	this.removeEventListener(Event.ENTER_FRAME, generatecomb);
	backbtn.removeEventListener(MouseEvent.CLICK, returncombine);
	gotoAndStop(1, "Save");
}

