this.addEventListener(Event.ENTER_FRAME, saveall);
function saveall(e:Event):void {
	for (number; number<=numbertotal; number++) {
		TextVariables["item"+number]=this["item"+number];
		TextVariables["type"+number]=this["type"+number];
		TextVariables["subtype"+number]=this["subtype"+number];
		TextVariables["color"+number]=this["color"+number];
		TextVariables["style"+number]=this["style"+number];
		TextVariables["season"+number]=this["season"+number];
		TextVariables["lastdate"+number]=this["lastdate"+number];
		TextVariables["lastdateold"+number]=this["lastdateold"+number];
		TextVariables["deltadays"+number]=this["deltadays"+number];
		TextVariables["show"+number]=this["show"+number];
		TextVariables["img"+number]=this["img"+number];
		trace(" ");
		trace(number);
		trace(TextVariables["item"+number]);
		trace(TextVariables["type"+number]);
		trace(TextVariables["subtype"+number]);
		trace(TextVariables["color"+number]);
		trace(TextVariables["style"+number]);
		trace(TextVariables["season"+number]);
		trace(TextVariables["lastdate"+number]);
		trace(TextVariables["lastdateold"+number]);
		trace(TextVariables["deltadays"+number]);
		trace(TextVariables["show"+number]);
		trace(TextVariables["img"+number]);
	}
	TextVariables["number"]=numbertotal;
	trace(" ");
	trace("Total: "+TextVariables["number"]);
	this.removeEventListener(Event.ENTER_FRAME,saveall);
	btn.addEventListener(MouseEvent.CLICK,save);

	/*if (number<numbertotal) {
