}

function save(e:Event):void {
	DataBase.save(TextVariables,"Clothes.txt");
	DataBase.addEventListener(Event.COMPLETE, onSaved);
	DataBase.addEventListener(Event.CANCEL, onCancel);
	btn.removeEventListener(MouseEvent.CLICK,save);
}

function onCancel(e:Event):void {
	trace("Canceled")
}

function onSaved(e:Event):void {
	setTimeout(wait4,500);
	function wait4() {
		gotoAndPlay(0, "Loader");
	}
}

