function PageFunc(intpage:int):int {
	if (intpage==0) {
		return (0);
	} else {
		return (intpage+12*(page-1));
	}
}

function PageFunc2(intpage:int):int {
	if (intpage==0) {
		return (0);
	} else if (int(intpage/12)-intpage/12==0) {
		return (intpage-12*(int(intpage/12)-1));
	} else {
		return (intpage-12*(int(intpage/12)));
	}
}

function TinAll(intnumb:int):void {
	for (var tp=1; tp<=pagetotal; tp++) {
		this["tin"+tp][0]=intnumb;
	}
}

