this.addEventListener(Event.ENTER_FRAME, loadall1);
function loadall1(e:Event):void {
	trace(number);
	//Está na página certa e ainda não passou o limite do número
	if (this.number>=12*(page-1)+1 && this.number<=12*page && this.number<=this.numbertotal) {
		//Menu editar e minis têm inis
		if (onedit==true&&this["tmini"+this.number]!=undefined){
			//O número do mini da página é o número do número geral
			this["mini"+(this.number-12*(page-1))].number=this["tin"+page][this.number-12*(page-1)]
		} else {
			//O número do mini da página é na ordem da listagem
			this["mini"+(this.number-12*(page-1))].number=this.itemsarray[this.number-1];
		}
		//Mostrar nome deste mini
		this["mini"+(this.number-12*(page-1))].showname=true;
		this["mini"+(this.number-12*(page-1))].thisgalpage=page;
		
		//Se as variáveis forem indefinidas
		if (this["tmini"+this.number]==undefined) {
			this["tmini"+this.number]=this.number;//O mini geral está no ini geral do mesmo numero
			this["tini"+this.number]=this.number;//O ini geral têm o mini geral do mesmo numero
			this["tni"+this.number]=this.number-12*(page-1);//O mini geral está no mini individual daqui
		}
		//Se o mini geral não está representado no 0 desta página
		if (this["tni"+this.number]!=0){
			//A posição do mini desta página é a posição da ini correspondente
			trace(this["tmini"+this.number])
			//this["mini"+this["tni"+this.number]].x=this["ini"+(this["tmini"+this.number]-12*(page-1))].x;
			//this["mini"+this["tni"+this.number]].y=this["ini"+(this["tmini"+this.number]-12*(page-1))].y;
		}
		trace("adding...");//12*(page) é o fim da página
		if ((this.numbertotal>=12*page && this.number==12*page) || (this.numbertotal<12*page && this.number==this.numbertotal)) {
			for (var ef=1+12*(page-1); ef<=12*page; ef++) {
				if (this["tmini"+ef]==undefined) {
					this["tmini"+ef]=ef;
					this["tini"+ef]=ef;
					this["tni"+ef]=ef-12*(page-1);
				}
			}
			trace("finish!");//12*(page-1)+1 é o início da página
			this.removeEventListener(Event.ENTER_FRAME, loadall1);
		} else {
			number=number+1;
		}
	} else if (this.number<=12*(page-1)+1 && this.number<12*page && this.number<this.numbertotal) {//Está na página anterior
		number=number+1;
		trace("not this...");
	}
}

