function dragfunction(e:Event):void {
	if (this["tini"+(Number(e.target.name.slice(3))+12*(page-1))]!=0){
		objecto=this["mini"+this["tni"+this["tini"+(Number(e.target.name.slice(3))+12*(page-1))]]]
		mouseposx=mouseX;
		mouseposy=mouseY;
		objposx=objecto.x;
		objposy=objecto.y;
		targposx=e.target.x;
		targposy=e.target.y;
		statuse="Dragging";
		notrep=true;
		trace(objecto.name, "number="+objecto.number, "contem="+this["t"+e.target.name], "casa="+this["t"+objecto.name]);
		stage.addEventListener(MouseEvent.MOUSE_UP, dropfunction);
	}
	e.target.removeEventListener(MouseEvent.MOUSE_DOWN, dragfunction);
}

function targetfunction(e:Event):void {
if (objecto.name=="mini0"){
	if (e.target.name!="ini0"){
		if (this["mini"+(Number(this["tini"+(Number(e.target.name.slice(3))+12*(page-1))])-12*(page-1))].number==0){
			//Largar mini0 num spot vazio
		} else {
			//Largar mini0 num spot com alguém
		} 
	} else if (e.target.name=="ini0"){
		//Largar mini0 no mesmo spot
		mini0.x=ini0.x
		mini0.y=ini0.y
		objecto=0;
		statuse="Waiting";
		notrep=true;
		e.target.removeEventListener(MouseEvent.MOUSE_UP, targetfunction);
	} 
} else if (objecto.name!="mini0"){
	if (e.target.name=="ini0"){
		if (mini0.number!=0){
			//Largar alguém no ini0 com alguém
		} else if (mini0.number==0){
			//Largar alguém no ini0 vazio
			objecto.x=objposx;
			objecto.y=objposy;
			this["tni0"]=objecto.number;
			this["tni"+objecto.number]=0;
			this["tin"+page][0]=objecto.number;
			this["tin"+page][Number(objecto.name.slice(4))]=0;
			mini0.number=objecto.number;
			objecto.number=0;
			obj_var=objecto;
			targ_var=ini0;
		}
	} else {
		//Não relacionado com o ini0 ou mini0
		obj_var=objecto;
		targ_var=e.target;
		trace("simple trade")
	}
}
	swapbackup1=this["tmini"+this["tin"+page][Number(obj_var.name.slice(4))]];//Casa onde está o que moveu
	swapbackup2=this["tmini"+this["tini"+(Number(targ_var.name.slice(3))+12*(obj_var.thisgalpage-1))]];//Casa alvo
	swapbackup3=this["tini"+(Number(targ_var.name.slice(3))+12*(obj_var.thisgalpage-1))];//Quem está na casa alvo
	swapbackup4=this["tini"+this["tmini"+this["tin"+page][Number(obj_var.name.slice(4))]]];//Quem moveu
	if (e.target.name!="ini0"&&this["tini"+this["tmini"+(Number(obj_var.name.slice(4))+12*(page-1))]]!=0){
		objecto.x=e.target.x;
		objecto.y=e.target.y;
		if (Number(this["tini"+(Number(targ_var.name.slice(3))+12*(page-1))]-12*(page-1))!=0){
			this["mini"+(Number(this["tini"+(Number(targ_var.name.slice(3))+12*(page-1))]-12*(page-1)))].x=targposx;
			this["mini"+(Number(this["tini"+(Number(targ_var.name.slice(3))+12*(page-1))]-12*(page-1)))].y=targposy;
		}
	}
	this["tmini"+this["tini"+(Number(targ_var.name.slice(3))+12*(obj_var.thisgalpage-1))]]=swapbackup1;
	this["tini"+this["tmini"+this["tin"+page][Number(obj_var.name.slice(4))]]]=swapbackup3;
	this["tmini"+this["tin"+page][Number(obj_var.name.slice(4))]]=swapbackup2;
	this["tini"+(Number(targ_var.name.slice(3))+12*(obj_var.thisgalpage-1))]=swapbackup4;
	objecto=0;
	statuse="Waiting";
	notrep=true;
	e.target.removeEventListener(MouseEvent.MOUSE_UP, targetfunction);
}

function dropfunction(e:Event):void {
	if (statuse!="Waiting") {
		objecto.x=objposx;
		objecto.y=objposy;
		objecto=0;
		statuse="Waiting";
		notrep=true;
		e.target.removeEventListener(MouseEvent.MOUSE_UP, dropfunction);
	}
}
