var number=1;
var page=1;
var pagechanged=false;
var thingselected=0;
var itemsarray:Array=[];

this.addEventListener(Event.ENTER_FRAME, loadall1);
function loadall1(e:Event):void {
	trace(number);
	if (this.number>=12*(page-1)+1 && this.number<12*page && this.number<this.numbertotal) {//Está na página certa
		//if (root["show"+this.itemsarray[this.number-1]]==1){
			this["mini"+(this.number-12*(page-1))].number=this.itemsarray[this.number-1];
			this["mini"+(this.number-12*(page-1))].showname=true;
			trace("adding...");//12*(page) é o fim da página
		//}
		number=number+1;
	} else if ((this.numbertotal>=12*page && this.number==12*page) || (this.numbertotal<12*page && this.number==this.numbertotal)) {
		this["mini"+(this.number-12*(page-1))].number=this.itemsarray[this.number-1];
		this["mini"+(this.number-12*(page-1))].showname=true;
		trace("finish!");//12*(page-1)+1 é o início da página
		this.removeEventListener(Event.ENTER_FRAME, loadall1);
	} else if (this.number<=12*(page-1)+1 && this.number<12*page && this.number<this.numbertotal) {//Está na página anterior
		number=number+1;
		trace("not this...");
	}
}

this.addEventListener(Event.ENTER_FRAME, pagechangedf);
function pagechangedf(e:Event):void {
	if (pagechanged==true) {
		pagechanged=false;
		this.removeEventListener(Event.ENTER_FRAME, loadall1);
		number=12*(page-1)+1;
		this.addEventListener(Event.ENTER_FRAME, loadall1);
	}
	if (mini1.thisselected==true){thingselected=mini1.number;mini1.thisselected=false;}
	if (mini2.thisselected==true){thingselected=mini2.number;mini2.thisselected=false;}
	if (mini3.thisselected==true){thingselected=mini3.number;mini3.thisselected=false;}
	if (mini4.thisselected==true){thingselected=mini4.number;mini4.thisselected=false;}
	if (mini5.thisselected==true){thingselected=mini5.number;mini5.thisselected=false;}
	if (mini6.thisselected==true){thingselected=mini6.number;mini6.thisselected=false;}
	if (mini7.thisselected==true){thingselected=mini7.number;mini7.thisselected=false;}
	if (mini8.thisselected==true){thingselected=mini8.number;mini8.thisselected=false;}
	if (mini9.thisselected==true){thingselected=mini9.number;mini9.thisselected=false;}
	if (mini10.thisselected==true){thingselected=mini10.number;mini10.thisselected=false;}
	if (mini11.thisselected==true){thingselected=mini11.number;mini11.thisselected=false;}
	if (mini12.thisselected==true){thingselected=mini12.number;mini12.thisselected=false;}
	if (thingselected!=0){
		this.removeEventListener(Event.ENTER_FRAME, loadall1);
		this.removeEventListener(Event.ENTER_FRAME, pagechangedf);
		for(var i=1; i<=12; i++){
			this["mini"+i].out=true;}
		}
}

